import React, { useState, useEffect } from 'react';
import PokerGame from './components/PokerGame';  // Assuming PokerGame.js is in the components folder
import './App.css';

function App() {
    // App-wide state
    const [appState, setAppState] = useState({
        status: 'Welcome to the Poker Game!', // Example app-wide state
    });

    // App-wide effects or other functionality can be defined here
    useEffect(() => {
        // Example: Setting up or fetching additional app-wide data on mount
        console.log("App component mounted.");
    }, []);

    // Optional function to handle app-wide actions, if there are any
    const handleAppAction = (action) => {
        console.log(`App action: ${action}`); // Log or handle app-wide actions
        // Potentially update app-wide state here
        setAppState({ ...appState, status: action });
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>{appState.status}</h1>  // Display app-wide status
                <PokerGame />  // Include the PokerGame component
            </header>
            <footer>
                <button onClick={() => handleAppAction('Refresh')}>Refresh Game</button>
                <p>Click refresh to restart the game!</p>
            </footer>
        </div>
    );
}

export default App;
