import React, { useState, useEffect } from 'react';
import PokerGame from './components/PokerGame';  // Make sure this path matches your file structure
import './App.css';

function App() {
    // state to control the start of the game
    const [startGame, setStartGame] = useState(false);

    // function to handle the game start
    const initiateGame = () => {
        setStartGame(true);
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>Welcome to the Poker Game!</h1>
                    <div>
                    <label>
                        Number of Opponents: {numOpponents}
                        <input type="range" min="1" max="9" value={numOpponents}
                            onChange={(e) => setNumOpponents(e.target.value)} />
                    </label>
                    <label>
                        Difficulty:
                        <select value={oppDifficulty} onChange={(e) => setOppDifficulty(e.target.value)}>
                            <option value="Amateur">Amateur</option>
                            <option value="Intermediate">Intermediate</option>
                            <option value="Expert">Expert</option>
                        </select>
                    </label>
                </div>
                {!startGame && (
                    <button onClick={initiateGame}>Start Game</button>
                )}
                {startGame && <PokerGame key={Date.now()} />}

            </header>
        </div>
    );
}

export default App;
